  //  })

 }

  render (){
  return (
    <div>
      <NewComment sendComment={this.sendComment}
      />
      <Comments comment={this.state.comment}/>
      { this.state.isLoading && <p>Carregando...</p>}
    </div>
  );
}
}
export default App;
