import React from 'react';
import { comment } from 'postcss-selector-parser';


class App extends React.Component {
 state = {
   comment: ['comment 1', 'comment 2', 'comment 3'],
   newComment: ''
 }

 sendComment = () => {
   this.setState({
    comment: [...this.state.comment, 'Novo comentarios' ]
   })
 }
  render (){
  return (
    <div>
      <div>
        <textarea></textarea>
        <button onClick={this.sendComment}>Enviar</button>
      </div>
      <div>
       {this.state.comment.map(c => {
         return <div>{c}</div>
       })}
      </div>
    </div>
  );
}
}
export default App;
