import React from 'react';
import { comment } from 'postcss-selector-parser';


class App extends React.Component {
 state = {
   comment: ['comment 1', 'comment 2', 'comment 3'],
   newComment: ''
 }

 sendComment = () => {
   this.setState({
    comment: [...this.state.comment, 'Novo comentarios' ]
   })
 }

 handleChange = (e) => {
   let value = e.target.value
   this.setState({newComment: value})

 }

  render (){
  return (
    <div>
      <div>
        <textarea value={this.state.newComment} onChange={this.handleChange(e)}></textarea>
        <button onClick={this.sendComment}>Enviar</button>
      </div>
      <div>
       {this.state.comment.map(c => {
         return <div>{c}</div>
       })}
      </div>
    </div>
  );
}
}
export default App;
